export default class GoogleSigninSampleApp extends Component {
  componentDidMount() {
    this._setupGoogleSignin()
  }

  render() {
    const { auth } = this.props
    if (!isLoaded(auth)) {
      return (

          Loading...

      )
    }
    if (!this.props.auth) {
      return (

           this._signIn()}
